import React, { useState } from 'react';
import LocationModal from './LocationModal';
import './Details.css';

const buttons = (props, modal, setModal) => {
  return props.buttons.map((b) => {
    if (b.endsWith('?')) {
      return (
        <button
          onClick={() => {
            props.setIcon(b);
            setModal(b);
          }}
          className="emergency-btn"
        >
          <img
            src={ require(`../../assets/imgs/${b}.png`) }
            className="icon"
          />
          <p className="emergency-btn-text">
            `${b}?`
          </p>
          <input
            checked={props.iconChecked === b}
            type="checkbox"
            className="checkbox"
          />
        </button>
      );
    } if (b === 'Crime') {
      return (
        <button
          onClick={() => {
            props.setIcon(b);
            setModal(b);
          }}
          className="emergency-btn"
        >
          <img
            src={ require(`../../assets/imgs/police-car.png`) }
            className="icon"
          />
          <p className="emergency-btn-text">
            Crime
          </p>
          <input
            checked={props.iconChecked === b}
            type="checkbox"
            className="checkbox"
          />
        </button>
      );
    } if (b === 'Unconscious') {
      return (
        <button
          onClick={() => {
            props.setIcon(b);
            setModal(b);
          }}
          className="emergency-btn"
        >
          <img
            src={ require(`../../assets/imgs/Unconscious.png`) }
          />
          <p className="emergency-btn-text">
            Unconscious
          </p>
          <input
            checked={props.iconChecked === b}
            type="checkbox"
            className="checkbox"
          />
        </button>
      );
    } if (b === 'Bus/Train') {
      return (
        <button
          onClick={() => {
            props.setIcon(b);
            setModal(b);
          }}
          className="emergency-btn"
        >
          <img
            src={ require(`../../assets/imgs/Bus.png`) }
            className="icon"
          />
          <p className="emergency-btn-text">
            Bus/Train?
          </p>
          <input
            checked={props.iconChecked === b}
            type="checkbox"
            className="checkbox"
          />
        </button>
      );
    } return (
      <button
        onClick={() => {
          props.setIcon(b);
          setModal(b);
        }}
        className="emergency-btn"
      >
        <img
          src={ require(`../../assets/imgs/${b}.png`) }
          className="icon"
        />
        <p className="emergency-btn-text">
          {b}
        </p>
        <input
          checked={props.iconChecked === b}
          type="checkbox"
          className="checkbox"
        />
      </button>
    );
  })
}

const DetailModal = (props) => {
  const [modal, setModal] = useState('');
  if (props.show) {
    return (
      <div>
      <LocationModal
        show={modal !== '' && props.location}
        header={`${modal}?`}
        para="(apt 4b, class 300a)"
        btnOneTxt="Cancel"
        btnTwoTxt="OK"
        btnOneClick={() => setModal('')}
        btnTwoClick={() => setModal('')}
      />
      {
        modal !== '' && props.location ? <div className="location-blur" /> : null
      }
      <div className="modal-container">
        <div className="modal-wrapper">
          <div className="modal-btn-container">
            <div className="modal-btn-text-container">
              <p className="modal-btn-text">
                {props.title}
              </p>
            </div>

            {buttons(props, modal, setModal)}
            <button
              onClick={() => props.dismiss()}
              className="blue emergency-btn"
            >
              <p className="emergency-btn-text">
                Cancel
              </p>
            </button>
          </div>
          <div className="next-btn-container">
            <button
              onClick={() => props.dismiss()}
              className="next-btn-wrapper"
            >
              <p className="next-btn">
                Next
              </p>
            </button>
          </div>
        </div>
      </div>
      </div>
    );
  } return null;
};

export default DetailModal;
